preRule:
//设置更新间隔时间,单位为整数天。
var uptime = 0;

//检测是否有写入时间与规则的js插件文件
if(!fetch("hiker://files/rules/xyq/hikerupdate.txt",{})||!fetch("hiker://files/rules/xyq/hikermovie.js",{})||!fetch("hiker://files/rules/xyq/hikermovie.json",{})){
var rulejs = fetch('https://code.aliyun.com/lzk23559/CloudRule/raw/master/hikermovie.js',{});
if(rulejs=='null'||rulejs=='undefined'||rulejs==''){rulejs = fetch('https://codeberg.org/lzk23559/cloudrule/raw/branch/master/hikermovie.js',{});}
writeFile("hiker://files/rules/xyq/hikermovie.js",rulejs);
var rulejson = fetch('https://code.aliyun.com/lzk23559/CloudRule/raw/master/hikermovie.json',{});
if(rulejson=='null'||rulejson=='undefined'||rulejson==''){rulejson = fetch('https://codeberg.org/lzk23559/cloudrule/raw/branch/master/hikermovie.json',{});}
writeFile("hiker://files/rules/xyq/hikermovie.json",rulejson);
writeFile("hiker://files/rules/xyq/hikerupdate.txt",new Date()+'')
}

//计算时间间隔
var start = new Date(fetch("hiker://files/rules/xyq/hikerupdate.txt",{})); 
var now = new Date(); 
var D_T = now.getTime() - start.getTime(); 
var D_D = D_T / (1000 * 3600 * 24);
var tdif=parseInt(D_D)+'';

//默认间隔超过设置天数就取一次云端文件
if(tdif>=uptime){
var rulejs = fetch('https://code.aliyun.com/lzk23559/CloudRule/raw/master/hikermovie.js',{});
if(rulejs==null||rulejs==undefined||rulejs==''){rulejs = fetch('https://codeberg.org/lzk23559/cloudrule/raw/branch/master/hikermovie.js',{});}
writeFile("hiker://files/rules/xyq/hikermovie.js",rulejs);
var rulejson = fetch('https://code.aliyun.com/lzk23559/CloudRule/raw/master/hikermovie.json',{});
if(rulejson==null||rulejson==undefined||rulejson==''){rulejson = fetch('https://codeberg.org/lzk23559/cloudrule/raw/branch/master/hikermovie.json',{});}
writeFile("hiker://files/rules/xyq/hikermovie.json",rulejson);
writeFile("hiker://files/rules/xyq/hikerupdate.txt",new Date()+'');
}

//执行需要预处理的站点
var lac=fetch('hiker://files/rules/xyq/hikermovie.js');
if(lac!=''){
eval(lac);hikerpre();}else{setError('规则不完整请尝试切换网络重试');}
